import urllib.request
import urllib.parse
from lxml import etree
import json
#百度百科Info
def Baike_info(html):
    dl_list = html.xpath(".//div[@class='basic-info J-basic-info cmn-clearfix']/dl")
    dic = {}
    for dl in dl_list:
        dt = dl.xpath('./dt/text()')
        dd = dl.xpath('./dd')
        d = [i.xpath('string()') for i in dd]
        for i in range(len(dt)):
            dic[dt[i].replace('\n','').replace('\xa0','')] = d[i].replace('\n','').replace('\xa0','')
    return dic

#百度百科正文paras
def Paras(html):
    divs = html.xpath('.//div[@class="main-content J-content"]/div')
    dic={}
    pos= []
    for i in range(len(divs)):
        try:
            if divs[i].xpath('@class')[0] == 'para-title level-2  J-chapter':
                pos.append(i)
        except:
            pass
    pos.append(len(divs))

    for i in range(len(pos)-1):
        start = pos[i]
        end = pos[i+1]
        #[i,end]  开始和结束位置
        for m in range(start,end):    #按照一级标题分段
            try:
                if divs[m].xpath('@class')[0] == 'para-title level-2  J-chapter':
                    h2 = divs[m].xpath('./h2/text()')[0]  # 获取一级标题
                    dic[h2] = {}
                    dic[h2][h2] = ''
                    for n in range(m+1,end):
                        try:
                            if divs[n].xpath('@class')[0] == 'para':
                                dic[h2][h2] += divs[n].xpath('string()').strip().replace('\n', '')
                                i +=1
                            else:
                                break
                        except:
                            pass
                elif divs[m].xpath('@class')[0] == 'para-title level-3  ':   # 二级标题
                    h3 = divs[m].xpath('./h3/text()')[0]  # 获取一级标题
                    dic[h2][h3] = ''
                    for o in range(m + 1, end):
                        if divs[o].xpath('@class')[0] == 'para':
                            dic[h2][h3] += divs[o].xpath('string()').strip().replace('\n', '')
                            i += 1
                        else:
                            break
                else:
                    pass
            except:
                pass
    return dic



#百度百科abstract
def Abstract(html):
    # 使用 xpath 匹配数据，得到匹配字符串列表
    sen_list = html.xpath('//div[contains(@class,"lemma-summary") or contains(@class,"lemmaWgt-lemmaSummary")]//text()')
    # 过滤数据，去掉空白
    sen_list_after_filter = [item.strip('\n') for item in sen_list]
    # 将字符串列表连成字符串并返回
    return ''.join(sen_list_after_filter)


def Baike_infohref(html):
    dic = {}
    a_list = html.xpath(".//div[@class='basic-info J-basic-info cmn-clearfix']/dl/dd/a")
    for a in a_list:
        if  a.xpath('@href'):
            value = 'https://baike.baidu.com' + a.xpath('@href')[0]
            key = a.xpath('string()')
            dic[key] = value
    return dic

# '孟加拉国': 'https://baike.baidu.com/item/%E5%AD%9F%E5%8A%A0%E6%8B%89%E5%9B%BD'
def query(content):
    dic ={}
    # 请求地址
    # url = 'https://baike.baidu.com/item/' + urllib.parse.quote(content)
    url = content
    # 请求头部
    headers = {
        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/67.0.3396.99 Safari/537.36'
    }
    # 利用请求地址和请求头部构造请求对象
    req = urllib.request.Request(url=url, headers=headers, method='GET')
    # 发送请求，获得响应
    response = urllib.request.urlopen(req)
    # 读取响应，获得文本
    text = response.read().decode('utf-8')
    # 构造 _Element 对象
    html = etree.HTML(text)

    #正文
    paras = Paras(html)
    dic['content'] = paras
    #info
    info = Baike_info(html)
    dic['info'] = info
    #摘要
    abs =Abstract(html)
    dic['abstract'] = abs

    #info_href
    href = Baike_infohref(html)
    dic['info_href'] = href
    print(dic)
    return (dic)

